create database cuahangmypham;
use cuahangmypham;

-- Tạo bảng vai trò
create table role (
    id_role int not null auto_increment,
    name_role varchar(30) not null,
    primary key (id_role)
);
-- Tạo bảng người dùng
create table users (
    id_users int not null auto_increment,
    fullname varchar(250) not null,
    email varchar(150) not null,
    id_role int not null,
    primary key (id_users)
);
-- Tạo bảng danh mục sản phẩm
create table category (
    id_category int not null auto_increment,
    name_category varchar(250) not null,
    primary key (id_category)
);
-- Tạo bảng sản phẩm
create table product (
    id_product int not null auto_increment,
    id_category int not null,
    title varchar(300) not null,
    price int not null,
    discount int,
    img varchar(500),
    descrip longtext,
    detail_descrip longtext,
    primary key (id_product)
);
-- Tạo bảng đơn hàng
create table orders (
    id_orders int not null,
    firstname varchar(125) not null,
    lastname varchar(125) not null,
    address varchar(200) not null,
    phone varchar(10) not null unique,
    email varchar(150) not null,
    note varchar(1000),
    order_date datetime,
    total int
);
-- Tạo bảng chi tiết đơn hàng
create table order_details (
    id_details int not null auto_increment,
    id_orders int not null,
	id_product int not null,
    price int,
    num int not null,
    total_deatail int,
    primary key (id_details)
);

